import CryptoJS from 'crypto-js';
export const SET_MAIN_TITLES = 'SET_MAIN_TITLES';
export const SET_PROFILE_TITLES = 'SET_PROFILE_TITLES';
export const SET_ACTIVE_INDEX = 'SET_ACTIVE_INDEX';
export const SET_ACTIVE_COMPONENT = 'SET_ACTIVE_COMPONENT';

export const setMainTitles = (titles) => ({
  type: SET_MAIN_TITLES,
  payload: titles,
});

export const setProfileTitles = (titles) => ({
  type: SET_PROFILE_TITLES,
  payload: titles,
});

export const setActiveIndex = (index) => ({
  type: SET_ACTIVE_INDEX,
  payload: index,
});

export const setActiveComponent = (component) => ({
  type: SET_ACTIVE_COMPONENT,
  payload: component,
});

export const getDecryptedRolesWithHeaders = (navigate) => {
  const encryptedRoles = localStorage.getItem('encryptedRoles');

  if (encryptedRoles) {
    try {
      const bytes = CryptoJS.AES.decrypt(
        encryptedRoles,
        '477f58bc13b97959097e7bda64de165ab9d7496b7a15ab39697e6d31ac61cbd1'
      );
      const roles = JSON.parse(bytes.toString(CryptoJS.enc.Utf8));

      const validRoles = ['super admin','admin', 'partner', 'hr'];
      const hasValidRole = roles.some(role =>
        validRoles.includes(role.toLowerCase())
      );

      if (!hasValidRole) {
        navigate('/');
        return [];
      }

      // Correctly structure headers
      return roles.map(role => {
        const headers = getHeadersForRole(role);
        return {
          role,
          mainTitles: headers.mainTitles || [],
          profileTitles: headers.profileTitles || [],
        };
      });
    } catch (error) {
      console.error("Error parsing roles or corrupted local storage data", error);
      localStorage.removeItem('encryptedRoles');
      localStorage.removeItem('authToken');
      navigate('/');
      return [];
    }
  }

  return [];
};


